    set_character(u"e" + id_addition, &shape("character-e" + shape_id_addition).radius(0.2).height(0.05).thickness(thickness).y(-0.125).subtract(&shape("character-e-" + shape_id_addition).width(0.3).height(0.2).x(0.25).y(-0.15)).add(&shape("character-e--" + shape_id_addition).height(line_thickness).width(0.4).y(-0.1).x(0.045)).build());
