_COMPONENT& text (string new_component_id, u16string characters, colour font_colour)
{
    u16string id_addition = to_u16string(font_colour.red) + to_u16string(font_colour.green) + to_u16string(font_colour.blue);

    if (!has_character(u"NULL" + id_addition)) throw runtime_error("\nERROR: A font with the colour" + to_string(font_colour.red) + "," + to_string(font_colour.green) + "," + to_string(font_colour.blue) + " has not been initialised.");

    component(new_component_id);
    u16string sub_string;
    _SHAPE* character;
    int current_slot = 0;
    int line_breaks = 0;
    int current_character = 0;
    float line_gap = 0;
    for (int i = 0; i < characters.length(); i++)
    {
        sub_string = characters.substr(i, 1);
        character = get_character(sub_string, font_colour);
        get_component(new_component_id).add(character).x(current_character, current_slot * 0.6f).y(current_character, line_gap);

        if (sub_string == u"\n")
        {
            current_slot = 0;
            line_breaks++;
            line_gap = -1.0f * line_breaks + line_breaks * -0.25f;
        }
        else current_slot += sub_string == u"\t" ? 2 : 1;

        current_character++;
    }
    return get_component(new_component_id);
}

void replace_text (string component_id, u16string new_text, colour new_colour)
{
    int old_size = get_component(component_id).get_shapes()->size();
    int new_size = new_text.length();
    if (old_size != new_size) throw runtime_error("\nERROR: The text of component '" + component_id + "' with length " + to_string(old_size) + " can not be replaced with text of length " + to_string(new_size) + ". The size has to be equal.");

    for (int i = 0; i < new_text.length(); i++)
    {
        get_component(component_id).replace(i, get_character(new_text.substr(i, 1), new_colour));
    }
}